time = Math.round(Date.now()/1000).toString()
function push_metric(what, target_type, value, dims) {
  if (value === null) {
    return;
  }

  metric = {
    'timestamp': time,
    'value': value,
    'properties': {'source': 'agent_monitoring', 'what': what, 'target_type': target_type, 'host_id': state['host_id'], 'streamsets_url': state['streamsets_url']},
    'tags': {'host_name': [state['host_name']], 'pipeline_id': ['Monitoring'], 'pipeline_type': ['monitoring']}
  }

  for (var dim in dims) {
    metric['properties'][dim] = dims[dim]
  }

  var newRecord = sdc.createRecord('id');
  newRecord.value = metric
  output.write(newRecord);
}

function get_value(curr_val, previous) {
  if (previous === null) {
    return null;
  }
  var diff = curr_val - previous
  if (diff < 0) {
    return null;
  }
  return diff
}

if (records.length > 0) {
  for(var i = 0; i < records.length; i++) {
    try {  
      var pipeline_id = records[i].value['pipelineId']
      if (pipeline_id == null && records[i].value['beans']) {
        var jvm_metrics = records[i].value['beans']
        for(var b = 0; b < jvm_metrics.length; b++) {
          if (jvm_metrics[b]['name'] == 'java.lang:type=Threading') {
            push_metric('live_threads', 'counter', jvm_metrics[b]['ThreadCount'])
          } else if (jvm_metrics[b]['name'] == 'java.lang:type=OperatingSystem') {
            push_metric('cpu_load', 'gauge', jvm_metrics[b]['ProcessCpuLoad'])  
		  } else if (jvm_metrics[b]['name'] == 'java.lang:type=Memory') {
            push_metric('heap_memory_usage', 'gauge', jvm_metrics[b]['HeapMemoryUsage']['used'])
            push_metric('non_heap_memory_usage', 'gauge', jvm_metrics[b]['NonHeapMemoryUsage']['used'])    
		  } else if (jvm_metrics[b]['name'] == 'java.lang:type=GarbageCollector,name=ConcurrentMarkSweep') {
            push_metric('ConcurrentMarkSweep_collections', 'gauge', jvm_metrics[b]['CollectionCount'])
            push_metric('ConcurrentMarkSweep_time', 'gauge', jvm_metrics[b]['CollectionTime'])       
		  } else if (jvm_metrics[b]['name'] == 'java.lang:type=GarbageCollector,name=ParNew') {
            push_metric('ParNew_collections', 'gauge', jvm_metrics[b]['CollectionCount'])
            push_metric('ParNew_time', 'gauge', jvm_metrics[b]['CollectionTime'])
		  }
        }

        if (records[i].value['kafka']) {
          var kafka_metrics = records[i].value['kafka']['beans']
          for(var b = 0; b < kafka_metrics.length; b++) {
            if (kafka_metrics[b]['records-lag-avg'] !== null) {
              var matches = kafka_metrics[b]['name'].match('topic=(.+),')
              if (matches !== null && matches.length > 1) {
                push_metric('records_lag_avg', 'gauge', kafka_metrics[b]['records-lag-avg'], {'topic': matches[1]})
              }
            }
          }
        }
        continue
      }
      if (pipeline_id.startsWith('Monitoring')) {
        timers = records[i].value['metrics']['timers']
        push_metric('health_check', 'counter', 1)
        push_metric('destination_latency', 'gauge', timers['stage.HTTPClient_03.batchProcessing.timer']['mean'])
        continue
      }
      if (records[i].value['metrics']['counters'] == null) {
        continue
      }
      counters = records[i].value['metrics']['counters']
      if (state['previous'][pipeline_id] == null) {
        state['previous'][pipeline_id] = {'batches': null, 'errors': null, 'input': null, 'output': null, 'last_timestamp_input': null, 'last_timestamp_output': null}
      }
      dims = {'pipeline_id': pipeline_id, 'pipeline_type': records[i].value['metadata']['labels'][0]}
      var val = get_value(counters['pipeline.batchCount.counter']['count'], state['previous'][pipeline_id]['batches'])
      push_metric('pipeline_batchCount', 'counter', val, dims)
      state['previous'][pipeline_id]['batches'] = counters['pipeline.batchCount.counter']['count']

      val = get_value(counters['pipeline.batchErrorRecords.counter']['count'], state['previous'][pipeline_id]['errors'])
      push_metric('pipeline_batchErrorRecords', 'counter', val, dims)
      state['previous'][pipeline_id]['errors'] = counters['pipeline.batchErrorRecords.counter']['count']

      if (records[i].value['metadata']['labels'].indexOf('influx') >= 0) {
        val = get_value(counters['stage.StreamSelector:destination_stream_lane.outputRecords.counter']['count'], state['previous'][pipeline_id]['input'])
        push_metric('pipeline_batchInputRecords', 'counter', val, dims)
        state['previous'][pipeline_id]['input'] = counters['stage.get_interval_records.outputRecords.counter']['count']

      } else {
        val = get_value(counters['pipeline.batchInputRecords.counter']['count'], state['previous'][pipeline_id]['input'])
        push_metric('pipeline_batchInputRecords', 'counter', val, dims)
        state['previous'][pipeline_id]['input'] = counters['pipeline.batchInputRecords.counter']['count']
      }

      val = get_value(counters['pipeline.batchOutputRecords.counter']['count'], state['previous'][pipeline_id]['output'])
      push_metric('pipeline_batchOutputRecords', 'counter', val, dims)
      state['previous'][pipeline_id]['output'] = counters['pipeline.batchOutputRecords.counter']['count']
    } catch (e) {
      // Send record to error
      error.write(records[i], e);
    }
  }
}
