state['TABLE_NAME'] = 'test';
state['TIMESTAMP_COLUMN'] = 'timestamp_unix';
state['DIMENSIONS'] = ['ver', 'AdSize', 'Country', 'AdType', 'Exchange'];
state['VALUES_COLUMNS'] = ['Clicks', 'Impressions'];
state['TARGET_TYPES'] = ['gauge', 'counter']
state['COUNT_RECORDS'] = 1

TARGET_TYPES = ['counter', 'gauge', 'running_counter']

function metric_to_string(dimensions) {
  var str = '';
    for (var p in dimensions) {
        if (dimensions.hasOwnProperty(p)) {
            str += p + ':' + dimensions[p] + ',';
        }
    }
    return str;
}

function get_running_counter_value(metric_properties, curr_value, time) {
  metric = metric_to_string(metric_properties)
  if (state['metrics'][metric] == null) {
    state['metrics'][metric] = {'val': curr_value, 'time': time}
    return null;
  }
  counter_val = curr_value - state['metrics'][metric]['val']
  if (counter_val < 0) {
    return null;
  }
  state['metrics'][metric]['val'] = curr_value
  prev_time = state['metrics'][metric]['time']
  state['metrics'][metric]['time'] = time
  return counter_val / (time - prev_time)
}

function get_target_type_value(target_type_property, record) {

  if (TARGET_TYPES.indexOf(target_type_property) < 0) {
    if (TARGET_TYPES.indexOf(record[target_type_property]) < 0) {
      throw 'Target type property ' + target_type_property + ' should be counter, gauge or running_counter'
    }
    return record[target_type_property]
  } 
  return target_type_property
}

for(var i = 0; i < records.length; i++) {
  try {

    // Create a new record with map field 
    var properties = {}
    for (var j = 0; j < state['DIMENSIONS'].length; j++) {
      if (records[i].value[state['DIMENSIONS'][j]] !== null && records[i].value[state['DIMENSIONS'][j]] !== '') {
        properties[state['DIMENSIONS_NAMES'][j].replace(/[\.\s]+/g, '_')] = String(records[i].value[state['DIMENSIONS'][j]]).trim().replace(/[\.\s]+/g, '_')
      }
    }

    for (var j = 0; j < state['VALUES_COLUMNS'].length; j++) {
      var newRecord = sdcFunctions.createRecord(records[i].sourceId + ':' + i);
      if (state['STATIC_WHAT']) {
        properties['what'] = state['MEASUREMENT_NAMES'][j].replace(/[\.\s]+/g, '_');
        properties['target_type'] = state['TARGET_TYPES'][j];
      } else {
        meas_name = records[i].value[state['MEASUREMENT_NAMES'][j]]
        if (typeof meas_name !== 'string') {
          throw state['MEASUREMENT_NAMES'][j] + ' property should be string instead of ' + (typeof meas_name)
        }
        properties['what'] = meas_name.replace(/[\.\s]+/g, '_');
        properties['target_type'] = get_target_type_value(state['TARGET_TYPES'][j], records[i].value);
	  }

      value = records[i].value[state['VALUES_COLUMNS'][j]]
      if (properties['target_type'] == 'running_counter') {
        properties['target_type'] = 'gauge';
        value = get_running_counter_value(properties, value, records[i].value[state['TIMESTAMP_COLUMN']]);

      }
      if (value === null) {
        continue;
      }
      newRecord.value = {'timestamp' : records[i].value[state['TIMESTAMP_COLUMN']],
                         'properties' : properties,
                         'value': value};
      output.write(newRecord);
    }
    if (state['COUNT_RECORDS']) {
      if (records[i].value[state['TIMESTAMP_COLUMN']] === null) {
        continue;
      }
      var newRecord = sdcFunctions.createRecord(records[i].sourceId + ':' + i);
      properties['what'] = state['COUNT_RECORDS_MEASUREMENT_NAME'];
      properties['target_type'] = 'counter';
      newRecord.value = {'timestamp' : records[i].value[state['TIMESTAMP_COLUMN']],
                         'properties' : properties,
                         'value': 1};
      output.write(newRecord);
    }

    // output.write(records[i]);
  } catch (e) {
    // Send record to error
    error.write(records[i], e);
  }
}
