configuration.value:
global sdc

try:
    sdc.importLock()
    import time
    from datetime import datetime, timedelta
finally:
    sdc.importUnlock()


def get_interval():
    return int(sdc.userParams['INTERVAL_IN_SECONDS'])


def get_now_with_delay():
    return int(time.time()) - int(sdc.userParams['DELAY_IN_SECONDS'])


def to_timestamp(date):
    epoch = datetime(1970, 1, 1)
    return int((date - epoch).total_seconds())


entityName = ''
interval = timedelta(seconds=get_interval())

if sdc.lastOffsets.containsKey(entityName):
    offset = int(float(sdc.lastOffsets.get(entityName)))
elif sdc.userParams['INITIAL_OFFSET']:
    offset = to_timestamp(datetime.strptime(sdc.userParams['INITIAL_OFFSET'], '%d/%m/%Y %H:%M'))
else:
    offset = to_timestamp(datetime.utcnow().replace(second=0, microsecond=0) - interval)

sdc.log.info('OFFSET: ' + str(offset))

while True:
    if sdc.isStopped():
        break
    now_with_delay = get_now_with_delay() - interval.total_seconds()
    if offset > now_with_delay:
        time.sleep(offset - now_with_delay)
    cur_batch = sdc.createBatch()
    record = sdc.createRecord('record created ' + str(datetime.now()))
    record.value = {'last_timestamp': int(offset)}
    offset += interval.total_seconds()
    cur_batch.add(record)
    cur_batch.process(entityName, str(offset))

state['TIMESTAMP_COLUMN'] = 'timestamp_datetime';
state['DIMENSIONS'] = ['adsize', 'country'];
state['DIMENSIONS_NAMES'] = ['adsize', 'country'];
state['VALUES_COLUMNS'] = ['clicks', 'impressions'];
state['MEASUREMENT_NAMES'] = ['clicks', 'impressions'];
state['TARGET_TYPES'] = ['gauge', 'gauge'];
state['COUNT_RECORDS'] = 0;
state['COUNT_RECORDS_MEASUREMENT_NAME'] = 'count';
state['STATIC_WHAT'] = 1;
state['VALUES_ARRAY_PATH'] = '';
state['VALUES_ARRAY_FILTER'] = [];
state['metrics'] = {}

for (var i = 0; i < records.length; i++) {
    var dimensions = {};
    for (var j = 0; j < state['DIMENSIONS'].length; j++) {
        var dimension = records[i].value[state['DIMENSIONS'][j]]
        var dimension_name = state['DIMENSIONS_NAMES'][j].replace(/[\. \/]+/g, '_')
        if (dimension !== null && dimension !== '') {
            dimensions[dimension_name] = String(dimension).replace(/[\. ]+/g, '_')
        }
    }

    try {
        if (records[i].value[state['TIMESTAMP_COLUMN']] === null) {
            continue;
        }
        var measurements = {}
        for (var j = 0; j < state['VALUES_COLUMNS'].length; j++) {
            var value = records[i].value[state['VALUES_COLUMNS'][j]];
            if (value === null) {
              continue;
            }
            var measurement = '';
            if (state['STATIC_WHAT']) {
                measurement = state['MEASUREMENT_NAMES'][j].replace(/[\. ]+/g, '_');
            } else {
                var meas_name = records[i].value[state['MEASUREMENT_NAMES'][j]]
                if (typeof meas_name !== 'string') {
                    throw state['MEASUREMENT_NAMES'][j] + ' property should be string instead of ' + (typeof meas_name)
                }
                measurement = meas_name.replace(/[\. ]+/g, '_');
            }
            measurements[measurement] = value;
        }
        if (state['COUNT_RECORDS']) {
            if (records[i].value[state['TIMESTAMP_COLUMN']] !== null) {
                measurements[state['COUNT_RECORDS_MEASUREMENT_NAME']] = 1;
            }
        }
        var newRecord = sdcFunctions.createRecord(records[i].sourceId + ':' + i);
        newRecord.value = {
             'timestamp': records[i].value[state['TIMESTAMP_COLUMN']],
             'dimensions': dimensions,
             'measurements': measurements,
             'schemaId': "${SCHEMA_ID}"
         };
         output.write(newRecord);
    } catch (e) {
        error.write(records[i], e);
    }
}

// get latest batch timestamp
for (var i = 0; i < records.length; i++) {
  output.write(records[i]);
}

if (records.length > 0) {
  last_record = records[records.length - 1];
  timestamp = last_record.value['timestamp'];
  event = sdc.createEvent('interval processed', 1);
  event.value = {
    'watermark': timestamp ,
  };
  sdc.toEvent(event);
}
