global sdc

try:
    sdc.importLock()
    import sys
    import os

    sys.path.append(os.path.join(os.environ['SDC_DIST'], 'python-libs'))
    import requests
    import traceback
    import time
    import urllib
    import re
finally:
    sdc.importUnlock()

entityName = ''
N_REQUESTS_TRIES = 3


def get_now_with_delay():
    return int(time.time()) - int(sdc.userParams['DELAY_IN_MINUTES']) * 60


def get_backfill_offset():
    if sdc.lastOffsets.containsKey(entityName):
        return int(float(sdc.lastOffsets.get(entityName)))
    if sdc.userParams['INITIAL_TIMESTAMP']:
        return int(sdc.userParams['INITIAL_TIMESTAMP'])
    return get_now_with_delay() - get_interval()


def get_interval():
    return int(sdc.userParams['INTERVAL'])


def make_request(url_):
    session = requests.Session()
    if sdc.userParams['USERNAME']:
        session.auth = (sdc.userParams['USERNAME'], sdc.userParams['PASSWORD'])
    for i in range(1, N_REQUESTS_TRIES + 1):
        try:
            sdc.log.debug(url_)
            res = session.get(
                url_,
                stream=True,
                headers={'Accept-Encoding': 'deflate'},
                verify=bool(sdc.userParams['VERIFY_SSL']),
                timeout=sdc.userParams['QUERY_TIMEOUT']
            )
            res.raise_for_status()
        except requests.HTTPError as e:
            requests.post(sdc.userParams['MONITORING_URL'] + str(e.response.status_code))
            sdc.log.error(str(e))
            if i == N_REQUESTS_TRIES:
                raise
            time.sleep(i**2)
            continue
        break
    return res


def get_result_key(data):
    return 'values' if data['data']['resultType'] == 'matrix' else 'value'


def get_metric_name(record):
    if '__name__' not in record['metric']:
        if not sdc.userParams['AGGREGATED_METRIC_NAME']:
            raise Exception("Victoria query result doesn't contain metric __name__ and it wasn't provided by the user")
        return sdc.userParams['AGGREGATED_METRIC_NAME']
    return record['metric']['__name__']


def process_matrix(result_, end_):
    batch = sdc.createBatch()
    for res in result_['data']['result']:
        base_record = dict(res['metric'].items())
        for timestamp, value in res[get_result_key(result_)]:
            record = base_record.copy()
            record['timestamp'] = float(timestamp)
            record['last_timestamp'] = end_
            metric_name = get_metric_name(res)
            record['__name__'] = metric_name
            record['__value'] = value
            sdc_record = sdc.createRecord('record created ' + str(get_now_with_delay()))
            sdc_record.value = record
            batch.add(sdc_record)
            if batch.size == sdc.batchSize:
                batch.process(entityName, str(end_))
                batch = sdc.createBatch()
    if batch.size > 0:
        batch.process(entityName, str(end_))


def process_vector(result_, end_):
    batch = sdc.createBatch()
    for res in result_['data']['result']:
        record = dict(res['metric'].items())
        timestamp, value = res[get_result_key(result_)]
        # here timestamp and end_ are the same values
        # because aggregation funcitons return timestamp from the end request parameter
        record['timestamp'] = timestamp
        record['last_timestamp'] = end_
        record['__name__'] = get_metric_name(res)
        record['__value'] = value
        sdc_record = sdc.createRecord('record created ' + str(get_now_with_delay()))
        sdc_record.value = record
        batch.add(sdc_record)
        if batch.size == sdc.batchSize:
            batch.process(entityName, str(end_))
            batch = sdc.createBatch()
    if batch.size > 0:
        batch.process(entityName, str(end_))


def main():
    interval = get_interval()
    end = get_backfill_offset() + interval
    while True:
        try:
            curr_url = get_base_url() + '&' + urllib.urlencode({'time': end})
            while end > get_now_with_delay():
                time.sleep(2)
                if sdc.isStopped():
                    return
            if sdc.isStopped():
                break
            sdc.log.debug(curr_url)
            res = make_request(curr_url).json()
            sdc.log.debug(str(res))
            process_matrix(res, end) if res['data']['resultType'] == 'matrix' else process_vector(res, end)
            end += interval
        except Exception:
            sdc.log.error(traceback.format_exc())
            raise


def get_base_url():
    return sdc.userParams['URL'] + '/api/v1/query?' + urllib.urlencode({
        'query': sdc.userParams['QUERY'].encode('utf-8'),
        'timeout': sdc.userParams['QUERY_TIMEOUT'],
    })


main()

state['TIMESTAMP_COLUMN'] = 'timestamp';
state['DIMENSIONS'] = {'instance': 'instance', 'location': 'location', 'job': 'job'};
state['MEASUREMENTS'] = {'__value': 'diff_dims'};
state['COUNT_RECORDS'] = 0;
state['COUNT_RECORDS_MEASUREMENT_NAME'] = 'count';
state['INTERVAL'] = 10000;
state['HEADER_ATTRIBUTES'] = [];
state['SEND_WATERMARK_IF_NO_DATA'] = 'False';

function extract_value(object, path) {
  path_parts = path.split('/')
  for (var k = 0; k < path_parts.length; k++) {
    object = object[path_parts[k]]
    if (object === null) {
      return null;
    }
  }
  if (object === null) {
    return 'NULL';
  }
  return object
}


function replace_illegal_chars(str) {
    return str.replace(/[\. \<]+/g, '_')
}

function get_dimensions(record) {
    var dimensions = {};
    for (var dimension_path in state['DIMENSIONS']) {
        var dimension_value = extract_value(record.value, dimension_path)
        if (dimension_value === null) {
            continue;
        }
        dimension_value = String(dimension_value).trim()
        if (dimension_value === '') {
            continue;
        }
        var dimension_name = replace_illegal_chars(state['DIMENSIONS'][dimension_path]).replace(/[\/]+/g, '_')
        dimensions[dimension_name] = replace_illegal_chars(dimension_value)
    }

    for (var k = 0; k < state['HEADER_ATTRIBUTES'].length; k++) {
        var attribute_value = record.attributes[state['HEADER_ATTRIBUTES'][k]]
        if (attribute_value === null) {
            continue;
        }
        attribute_value = String(attribute_value).trim()
        if (attribute_value === '') {
            continue;
        }
        dimensions[replace_illegal_chars(state['HEADER_ATTRIBUTES'][k])] = replace_illegal_chars(attribute_value)
    }
    return dimensions
}

function get_measurements(record) {
    var measurements = {}
    for (var measurement_path in state['MEASUREMENTS']) {
        var value = extract_value(record, measurement_path);
        if (value === null || value === '' || isNaN(value)) {
            continue;
        }
        if (typeof value === 'string' || value instanceof String) {
            value = parseFloat(value)
        }
        measurements[state['MEASUREMENTS'][measurement_path]] = value;
    }
    if (state['COUNT_RECORDS']) {
        measurements[state['COUNT_RECORDS_MEASUREMENT_NAME']] = 1;
    }
    return measurements
}

function last_timestamp_only(record) {
    var record_keys = [];
    for (var key in record) {
        record_keys.push(key)
    }
    return (record_keys.length === 1 && record_keys[0] === 'last_timestamp');
}

if (records.length === 1 && last_timestamp_only(records[0].value)) {
