            };
            output.write(newRecord);
        } catch (e) {
            error.write(records[i], e);
        }
    }
}

if (records.length > 0 && (state['SEND_WATERMARK_IF_NO_DATA'] === 'True' || extract_value(records[0].value, state['TIMESTAMP_COLUMN']))) {
