global sdc

try:
    sdc.importLock()
    import sys
    import os
    import time
    import traceback

    sys.path.append(os.path.join(os.environ['SDC_DIST'], 'python-libs'))
    import requests
    import urlparse

    from datetime import datetime, timedelta
    from requests.auth import HTTPBasicAuth
finally:
    sdc.importUnlock()

# single threaded - no entityName because we need only one offset
entityName = ''
DATEFORMAT = '%Y-%m-%dT%H:%M:%SZ'
SOLARWINDS_API_ADDRESS = '/SolarWinds/InformationService/v3/Json/Query'
LAST_TIMESTAMP = '%last_timestamp%'


# Jython converts datetime objects to java.sql.Timestamp when assigning it to a variable
def date_from_str(date):
    return datetime.strptime(date, DATEFORMAT)


def date_to_str(date):
    return date.strftime(DATEFORMAT)


def get_now_with_delay():
    return int(time.time()) - int(sdc.userParams['DELAY_IN_SECONDS'])


def to_timestamp(date):
    epoch = datetime(1970, 1, 1)
    return int((date - epoch).total_seconds())


def get_interval():
    return int(sdc.userParams['INTERVAL_IN_SECONDS'])


if sdc.lastOffsets.containsKey(entityName):
    offset = int(float(sdc.lastOffsets.get(entityName)))
elif sdc.userParams['DAYS_TO_BACKFILL']:
    offset = to_timestamp(datetime.utcnow().replace(hour=0, minute=0, second=0, microsecond=0) - timedelta(days=int(sdc.userParams['DAYS_TO_BACKFILL'])))
else:
    offset = to_timestamp(datetime.now().replace(second=0, microsecond=0))

sdc.log.info('Start offset: ' + str(offset))

cur_batch = sdc.createBatch()

N_REQUESTS_TRIES = 3

while True:
    try:
        end = offset + get_interval()
        if end > get_now_with_delay():
            time.sleep(end - get_now_with_delay())
        query = sdc.userParams['QUERY'].replace(LAST_TIMESTAMP, date_to_str(datetime.fromtimestamp(offset)))

        for i in range(1, N_REQUESTS_TRIES + 1):
            try:
                res = requests.get(
                    sdc.userParams['SOLARWINDS_API_URL'] + SOLARWINDS_API_ADDRESS,
                    auth=HTTPBasicAuth(sdc.userParams['API_USER'], sdc.userParams['API_PASSWORD']),
                    params={'query': query},
                    verify=False,
                    timeout=sdc.userParams['QUERY_TIMEOUT'],
                )
                raise requests.HTTPError('test')
                res.raise_for_status()
                break
            except requests.HTTPError as e:
                requests.post(sdc.userParams['MONITORING_URL'] + str(res.status_code))
                sdc.log.error(str(e))
                if i == N_REQUESTS_TRIES:
                    raise
                time.sleep(2 ** i)

        for row in res.json()['results']:
            record = sdc.createRecord('record created ' + str(datetime.now()))
            record.value = row
            cur_batch.add(record)

        # send batch and save offset
        offset = end
        cur_batch.process(entityName, str(offset))
        cur_batch = sdc.createBatch()
        if sdc.isStopped():
            break
    except Exception as e:
        sdc.log.error(traceback.format_exc())
        raise

if cur_batch.size() + cur_batch.errorCount() + cur_batch.eventCount() > 0:
    cur_batch.process(entityName, str(offset))

state['TIMESTAMP_COLUMN'] = 'DateTime';
state['DIMENSIONS'] = ['NodeID'];
state['DIMENSIONS_NAMES'] = ['NodeID'];
state['VALUES_COLUMNS'] = ['MinMemoryUsed', 'AvgPercentMemoryUsed'];
state['MEASUREMENT_NAMES'] = ['MinMemoryUsed', 'AvgPercentMemoryUsed'];
state['TARGET_TYPES'] = ['gauge', 'gauge'];
state['COUNT_RECORDS'] = 0;
state['COUNT_RECORDS_MEASUREMENT_NAME'] = 'count';
state['STATIC_WHAT'] = 1;
state['VALUES_ARRAY_PATH'] = '';
state['VALUES_ARRAY_FILTER'] = [];
state['INTERVAL'] = 86400;
state['metrics'] = {}

TARGET_TYPES = ['counter', 'gauge', 'running_counter']

function get_value(path, value) {
  path_parts = path.split('/')
  for (var k = 0; k < path_parts.length; k++) {
    value = value[path_parts[k]]
  }
  return value
}

function metric_to_string(dimensions) {
  var str = '';
    for (var p in dimensions) {
        if (dimensions.hasOwnProperty(p)) {
            str += p + ':' + dimensions[p] + ',';
        }
    }
    return str;
}

function get_running_counter_value(metric_properties, curr_value, time) {
  metric = metric_to_string(metric_properties)
  if (state['metrics'][metric] == null) {
    state['metrics'][metric] = {'val': curr_value, 'time': time}
    return null;
  }
  counter_val = curr_value - state['metrics'][metric]['val']
  if (counter_val < 0) {
    return null;
  }
  state['metrics'][metric]['val'] = curr_value
  prev_time = state['metrics'][metric]['time']
  state['metrics'][metric]['time'] = time
  return counter_val / (time - prev_time)
}

function get_target_type_value(target_type_property, record) {

  if (TARGET_TYPES.indexOf(target_type_property) < 0) {
    if (TARGET_TYPES.indexOf(record[target_type_property]) < 0) {
      throw 'Target type property ' + target_type_property + ' should be counter, gauge or running_counter'
    }
    return get_value(target_type_property, record)
  } 
  return target_type_property
}

for(var i = 0; i < records.length; i++) {
  try {
	var timestamp = get_value(state['TIMESTAMP_COLUMN'], records[i].value)

    var properties = {}
    for (var j = 0; j < state['DIMENSIONS'].length; j++) {
      var dim_val = get_value(state['DIMENSIONS'][j], records[i].value)
      if (dim_val !== null && String(dim_val).trim() !== '') {
        dim_val = String(dim_val).substring(0, 150)
        properties[state['DIMENSIONS_NAMES'][j].replace(/[\.\s]+/g, '_')] = dim_val.trim().replace(/[\.\s]+/g, '_')
      }
    }

    for (var j = 0; j < state['VALUES_COLUMNS'].length; j++) {

      var values_array = [records[i].value]
      if (state['VALUES_ARRAY_PATH'].length > 0) {
        values_array = get_value(state['VALUES_ARRAY_PATH'], records[i].value)
      }
      for (var l = 0; l < values_array.length; l++) {
        if (state['STATIC_WHAT']) {
          properties['what'] = state['MEASUREMENT_NAMES'][j].replace(/[\.\s]+/g, '_');
          properties['target_type'] = state['TARGET_TYPES'][j];
        } else {
          meas_name = get_value(state['MEASUREMENT_NAMES'][j], values_array[l])
          if (state['VALUES_ARRAY_FILTER'].length > 0 && state['VALUES_ARRAY_FILTER'].indexOf(meas_name) < 0) {
            continue;
          }
          properties['what'] = String(meas_name).replace(/[\.\s]+/g, '_');
          properties['target_type'] = get_target_type_value(state['TARGET_TYPES'][j], values_array[l]);
        }

        var newRecord = sdcFunctions.createRecord(records[i].sourceId + ':' + i);
        var value = get_value(state['VALUES_COLUMNS'][j], values_array[l])
        if (properties['target_type'] == 'running_counter') {
          properties['target_type'] = 'gauge';
          value = get_running_counter_value(properties, value, timestamp);
          if (value === null) {
            continue;
          }
        }
		if (value === null) {
            continue;
        } 
        newRecord.value = {'timestamp' : timestamp,
                           'properties' : properties,
                           'value': value};
        output.write(newRecord);
      }
    }
    if (state['COUNT_RECORDS']) {
      var newRecord = sdcFunctions.createRecord(records[i].sourceId + ':' + i);
      properties['what'] = state['COUNT_RECORDS_MEASUREMENT_NAME'];
      properties['target_type'] = 'counter';
      newRecord.value = {'timestamp' : timestamp,
                         'properties' : properties,
                         'value': 1};
      output.write(newRecord);
    }

    // output.write(records[i]);
  } catch (e) {
    // Send record to error
    error.write(records[i], e);
  }
}
