time = Math.round(Date.now()/1000).toString()
function get_metric(pipeline_id, what, target_type, value, pipeline_type) {
  metric = {
    'timestamp': time,
    'value': value,
    'properties': {'source': 'agent_monitoring', 'what': what, 'target_type': target_type, 'host_id': state['host_id']},
    'tags': {'host_name': [state['host_name']], 'pipeline_id': ['Monitoring'], 'pipeline_type': ['monitoring']}
  }
  if (pipeline_id) {
    metric['properties']['pipeline_id'] = pipeline_id
  }
  if (pipeline_type) {
    metric['properties']['pipeline_type'] = pipeline_type
  }
  return metric
}

function get_value(curr_val, previous) {
  var diff = curr_val - previous
  if (diff < 0) {
    return curr_val;
  }
  return diff
}

if (records.length > 0) {
  var newRecord = sdcFunctions.createRecord(records[0].sourceId + ':newRecordId');
  newRecord.value = []
  for(var i = 0; i < records.length; i++) {
    try {  
      var pipeline_id = records[i].value['pipelineId']
      if (pipeline_id == null && records[i].value['beans']) {
        var jvm_metrics = records[i].value['beans']
        for(var b = 0; b < jvm_metrics.length; b++) {
          if (jvm_metrics[b]['name'] == 'java.lang:type=Threading') {
            newRecord.value.push(get_metric(null, 'live_threads', 'counter', jvm_metrics[b]['ThreadCount'], null))
          } else if (jvm_metrics[b]['name'] == 'java.lang:type=OperatingSystem') {
            newRecord.value.push(get_metric(null, 'cpu_load', 'gauge', jvm_metrics[b]['ProcessCpuLoad'], null)     )   
		  } else if (jvm_metrics[b]['name'] == 'java.lang:type=Memory') {
            newRecord.value.push(get_metric(null, 'heap_memory_usage', 'gauge', jvm_metrics[b]['HeapMemoryUsage']['used'], null))
            newRecord.value.push(get_metric(null, 'non_heap_memory_usage', 'gauge', jvm_metrics[b]['NonHeapMemoryUsage']['used'], null)  )      
		  } else if (jvm_metrics[b]['name'] == 'java.lang:type=GarbageCollector,name=ConcurrentMarkSweep') {
            newRecord.value.push(get_metric(null, 'ConcurrentMarkSweep_collections', 'gauge', jvm_metrics[b]['CollectionCount'], null))
            newRecord.value.push(get_metric(null, 'ConcurrentMarkSweep_time', 'gauge', jvm_metrics[b]['CollectionTime'], null))        
		  } else if (jvm_metrics[b]['name'] == 'java.lang:type=GarbageCollector,name=ParNew') {
            newRecord.value.push(get_metric(null, 'ParNew_collections', 'gauge', jvm_metrics[b]['CollectionCount'], null))
            newRecord.value.push(get_metric(null, 'ParNew_time', 'gauge', jvm_metrics[b]['CollectionTime'], null))      
		  }
        }
        continue
      }
      if (pipeline_id == 'Monitoring') {
        timers = records[i].value['metrics']['timers']
        newRecord.value.push(get_metric(null, 'health_check', 'counter', 1))
        newRecord.value.push(get_metric(null, 'destination_latency', 'gauge', timers['stage.HTTPClient_03.batchProcessing.timer']['mean']))
        continue
      }
      if (records[i].value['metrics'] == null) {
        continue
      }
      counters = records[i].value['metrics']['counters']
      if (state['previous'][pipeline_id] == null) {
        state['previous'][pipeline_id] = {'batches': 0, 'errors': 0, 'input': 0, 'output': 0}
      }
      pipeline_type = records[i].value['metadata']['labels'][0]
      var val = get_value(counters['pipeline.batchCount.counter']['count'], state['previous'][pipeline_id]['batches'])
      newRecord.value.push(get_metric(pipeline_id, 'pipeline_batchCount', 'counter', val, pipeline_type))
      state['previous'][pipeline_id]['batches'] = val
      val = get_value(counters['pipeline.batchErrorRecords.counter']['count'], state['previous'][pipeline_id]['errors'])
      newRecord.value.push(get_metric(pipeline_id, 'pipeline_batchErrorRecords', 'counter', val, pipeline_type))
      state['previous'][pipeline_id]['errors'] = val

      if (records[i].value['metadata']['labels'].indexOf('influx') >= 0) {
        val = get_value(counters['stage.StreamSelector_01:destination_stream_lane.outputRecords.counter']['count'], state['previous'][pipeline_id]['input'])
        newRecord.value.push(get_metric(pipeline_id, 'pipeline_batchInputRecords', 'counter', val, pipeline_type))
        state['previous'][pipeline_id]['input'] = val
        val = get_value(counters['stage.destination.inputRecords.counter']['count'], state['previous'][pipeline_id]['output'])
        newRecord.value.push(get_metric(pipeline_id, 'pipeline_batchOutputRecords', 'counter', val, pipeline_type))
        state['previous'][pipeline_id]['output'] = val
      } else {
        val = get_value(counters['pipeline.batchInputRecords.counter']['count'], state['previous'][pipeline_id]['input'])
        newRecord.value.push(get_metric(pipeline_id, 'pipeline_batchInputRecords', 'counter', val, pipeline_type))
        state['previous'][pipeline_id]['input'] = val
        val = get_value(counters['pipeline.batchOutputRecords.counter']['count'], state['previous'][pipeline_id]['output'])
        newRecord.value.push(get_metric(pipeline_id, 'pipeline_batchOutputRecords', 'counter', val, pipeline_type))
        state['previous'][pipeline_id]['output'] = val
      }

    } catch (e) {
      // Send record to error
      error.write(records[i], e);
    }
  }

  try {
    output.write(newRecord);
  } catch (e) {
    // Send record to error
    error.write(newRecord, e);
  }

  var event = sdcFunctions.createEvent('clean-errors', 1);
  event.value = {'check-errors': '1'};
  sdcFunctions.toEvent(event);
}
